sys_ux_data_broker_graphql.query:
query ($vehicleSysId: ID!) {
    xSncFltMgmt {
        fleet {
            vehicle(id: $vehicleSysId) {
                maintReqs {
                    id
                    number
                    shortDescription
                    assignedTo {
                        id
                        name
                    }
                    maintTasks {
                        id
                        number
                        shortDescription
                        assignedTo {
                            id
                            name
                        }
                    }
                }
                parts {
                    serialNumber
                    partName
                }
            }
        }
    }
}

sys_ux_data_broker_graphql.script:
(function buildQuery(query, inputs) { 
         var qb = new GraphQLQueryBuilder(query, inputs);
   // return appropriate query here
    return qb; 
   })(query, inputs);
